"use strict";

var menuBtn = document.querySelector(".main-header__button-menu"), navbar = document.querySelector(".navbar");

function openMenu() {
    navbar.classList.toggle("navbar__hide");
}

function closeMenu() {
    navbar.classList.add("navbar__hide");
}

menuBtn.addEventListener("click", function() {
    openMenu();
}), window.addEventListener("DOMContentLoaded", function() {
    closeMenu();
});

var showModalBtns = document.querySelectorAll(".products__button--calc"), overlay = document.querySelector(".overlay"), modal = document.querySelector(".modal-feedback"), closeModalBtn = document.querySelector(".modal-feedback__close");

function showModal(e) {
    e.preventDefault(), overlay.classList.add("overlay-show"), modal.classList.add("modal-feedback__show");
}

function closeModal(e) {
    e.preventDefault(), overlay.classList.remove("overlay-show"), modal.classList.remove("modal-feedback__show");
}

showModalBtns.forEach(function(e) {
    e.addEventListener("click", function() {
        showModal(event);
    });
}), closeModalBtn.addEventListener("click", function() {
    closeModal(event);
});

var submitIntroBtn = document.querySelector(".intro__button"), submitModalBtn = document.querySelector(".modal-feedback__button");

function sendData(e) {
    e.preventDefault();
}

submitIntroBtn.addEventListener("click", function() {
    sendData(event);
}), submitModalBtn.addEventListener("click", function() {
    sendData(event);
});